<template>
  <div id="app">
    <div class="left-menu" :style="{ top: `${menuTop}px` }">
      <ul>
        <li @click="navigateTo('/')">첫화면</li>
        <li @click="navigateTo('/db-list')">DB 전체 리스트</li>
        <li @click="navigateTo('/sms-history')">SMS 전송 내역</li>
        <li @click="navigateTo('/threshold-list')">임계치 리스트</li>
        <li @click="navigateTo('/tablespaces')">테이블스페이스 리스트</li>
        <li @click="navigateTo('/dailyChk')">일일 점검(hit율)</li>
      </ul>
    </div>
    <div class="right-content">
      <router-view /> <!-- 라우팅된 컴포넌트를 렌더링 -->
    </div>
  </div>
</template>

<script>
import { ref, onMounted, onUnmounted } from 'vue'
import { useRouter } from 'vue-router'

export default {
  setup() {
    const router = useRouter()
    const menuTop = ref(0) // 메뉴의 현재 위치

    const handleScroll = () => {
      const offsetTop = 0 // 초기 상단 여백
      menuTop.value = Math.max(offsetTop, window.scrollY) // 스크롤에 따라 top 값 변경
    }

    onMounted(() => {
      window.addEventListener('scroll', handleScroll)
    })

    onUnmounted(() => {
      window.removeEventListener('scroll', handleScroll)
    })

    const navigateTo = (path) => {
      router.push(path)
    }

    return { navigateTo, menuTop }
  }
}
</script>

