// TablespacesList.vue
<template>
  <div>
    <h2>{{ selectedDb }} DB - 테이블스페이스 리스트</h2>

    <!-- DB 목록을 보여주는 드롭다운 -->
    <select v-model="selectedDb" @change="fetchTablespaces(selectedDb)">
      <option v-for="(db, index) in dbList" :key="index" :value="db">
        {{ db }}
      </option>
    </select>

    <!-- 검색 입력 필드 -->
    <input
      v-model="searchQuery"
      type="text"
      placeholder="테이블스페이스 검색"
      style="margin-bottom: 10px; padding: 5px; width: 300px;"
    />

    <table>
      <thead>
        <tr>
          <th @click="sortData('tsName')">Tablespace 이름</th>
          <th @click="sortData('totalSize')">총 크기 (MB)</th>
          <th @click="sortData('usedSize')">사용량 (MB)</th>
          <th @click="sortData('freeSize')">남은 크기 (MB)</th>
          <th @click="sortData('usedRate')">사용률 (%)</th>
          <th @click="sortData('chkDate')">체크 날짜</th>
          <th @click="sortData('chkTime')">체크 시간</th>
          <th @click="sortData('autoExtensCntFile')">자동 확장 파일 수</th>
          <th @click="sortData('maxMbBytes')">최대 MB 바이트</th>
        </tr>
      </thead>
      <tbody>
        <tr v-for="(ts, index) in filteredTablespaces" :key="index">
          <td>{{ ts.tsName }}</td> <!-- tsName -->
          <td>{{ ts.totalSize }}</td> <!-- totalSize -->
          <td>{{ ts.usedSize }}</td> <!-- usedSize -->
          <td>{{ ts.freeSize }}</td> <!-- freeSize -->
          <td>{{ ts.usedRate }}</td> <!-- usedRate -->
          <td>{{ ts.chkDate }}</td> <!-- chkDate -->
          <td>{{ ts.chkTime }}</td> <!-- chkTime -->
          <td>{{ ts.autoExtensCntFile }}</td> <!-- autoExtensCntFile -->
          <td>{{ ts.maxMbBytes }}</td> <!-- maxMbBytes -->
        </tr>
      </tbody>
    </table>

    <p v-if="filteredTablespaces.length === 0">검색 결과가 없습니다.</p>
  </div>
</template>

<script>
import axios from "axios";

export default {
  data() {
    return {
      selectedDb: null, // 선택된 DB 이름
      dbList: [], // DB 목록
      tablespaces: [], // 테이블스페이스 데이터
      searchQuery: "", // 검색어
      sortKey: "tsName", // 정렬 키 (기본값을 'tsName'으로 설정)
      sortOrder: 1, // 정렬 순서 (1: 오름차순, -1: 내림차순)
    };
  },
  computed: {
    filteredTablespaces() {
      // 필터링 로직: 검색어에 맞는 테이블스페이스만 표시
      let result = this.tablespaces.filter((ts) =>
        ts.tsName && ts.tsName.toLowerCase().includes(this.searchQuery.toLowerCase())
      );
      return result.sort((a, b) => {
        const aValue = a[this.sortKey];
        const bValue = b[this.sortKey];

        // 숫자 비교 시 안전하게 처리
        if (aValue === undefined) return 1 * this.sortOrder;
        if (bValue === undefined) return -1 * this.sortOrder;

        if (aValue < bValue) return -1 * this.sortOrder;
        if (aValue > bValue) return 1 * this.sortOrder;
        return 0;
      });
    }
  },
  methods: {
    // DB 목록을 가져오는 함수
    fetchDbList() {
      axios
        .get("/api/tb/list")
        .then((res) => {
          this.dbList = res.data;
          if (this.dbList.length > 0) {
            this.selectedDb = this.dbList[0]; // 첫 번째 DB를 자동으로 선택
            this.fetchTablespaces(this.selectedDb); // 선택한 DB에 대해 테이블스페이스 데이터 가져오기
          }
        })
        .catch((error) => {
          console.error("Error fetching DB list:", error);
        });
    },

    // 선택된 DB에 해당하는 테이블스페이스 데이터 가져오는 함수
    fetchTablespaces(dbName) {
      this.selectedDb = dbName; // 선택된 DB를 저장
      this.tablespaces = []; // 이전 데이터를 초기화
      axios
        .get(`/api/tb/${dbName}/tablespaces`)
        .then((res) => {
          console.log("서버 응답 데이터:", res.data); // 응답 데이터를 콘솔에 출력
          if (Array.isArray(res.data) && res.data.length > 0) {
            this.tablespaces = res.data;
          } else {
            console.log("받은 데이터가 비어있거나 배열 형식이 아닙니다.");
          }
        })
        .catch((error) => {
          console.error("테이블스페이스 데이터 가져오기 실패:", error);
        });
    },

    // 테이블 데이터 정렬
    sortData(key) {
      if (this.sortKey === key) {
        this.sortOrder *= -1; // 동일 키를 클릭하면 정렬 순서 변경
      } else {
        this.sortKey = key; // 새 키로 정렬
        this.sortOrder = 1;
      }
    },
  },
  mounted() {
    this.fetchDbList(); // 컴포넌트가 마운트되면 DB 목록을 가져옵니다.
  },
};
</script>
