import DBList from './components/DBList.vue'
import SmsHistory from './components/SmsHistory.vue'
import ThresholdList from './components/ThresholdList.vue'

<template>
  <div id="app">
    <div class="left-menu">
      <ul>
        <li @click="getDBList">DB 전체 리스트</li>
        <li @click="getSmsHistory">SMS 전송 내역</li>
        <li @click="getThresholdList">임계치 리스트</li>
      </ul>
    </div>
    <div class="right-content">
      <div v-if="currentPage === 'DBList'">
        <DBList :data="dbList"/>
      </div>
      <div v-if="currentPage === 'SmsHistory'">
        <SmsHistory :data="smsHistory"/>
      </div>
      <div v-if="currentPage === 'ThresholdList'">
        <ThresholdList :data="thresholdList"/>
      </div>
    </div>
  </div>
</template>

<script>
import axios from 'axios'
import DBList from './components/DBList'
import SmsHistory from './components/SmsHistory'
import ThresholdList from './components/ThresholdList'

export default {
  components: {
    DBList,
    SmsHistory,
    ThresholdList
  },
  data() {
    return {
      currentPage: 'DBList',
      dbList: [],
      smsHistory: [],
      thresholdList: [],
    }
  },
  methods: {
    getDBList() {
      axios.get('/api/db-list/all').then(response => {
        this.dbList = response.data
        this.currentPage = 'DBList'
      })
    },
    getSmsHistory() {
      axios.get('/api/sms-history').then(response => {
        this.smsHistory = response.data
        this.currentPage = 'SmsHistory'
      })
    },
    getThresholdList() {
      axios.get('/api/threshold/all').then(response => {
        this.thresholdList = response.data
        this.currentPage = 'ThresholdList'
      })
    }
  }
}
</script>
