// App.vue
<template>
  <div id="app">
    <div class="left-menu" :style="{ top: `${menuTop}px` }">
      <ul>
        <li @click="navigateTo('/')">첫화면</li>
        <li @click="navigateTo('/db-list')">DB 전체 리스트</li>
        <li @click="navigateTo('/sms-history')">SMS 전송 내역</li>
        <li @click="navigateTo('/threshold-list')">임계치 리스트</li>
        <li @click="navigateTo('/tablespaces')">테이블스페이스 리스트</li>
        <li @click="navigateTo('/dailyChk')">일일 점검(hit율)</li>
      </ul>
    </div>
    <div class="right-content">
      <router-view /> <!-- 라우팅된 컴포넌트를 렌더링 -->
    </div>
  </div>
</template>

<script>
import { ref, onMounted, onUnmounted } from 'vue'
import { useRouter } from 'vue-router'

export default {
  setup() {
    const router = useRouter()
    const menuTop = ref(20) // 메뉴의 현재 위치

    const handleScroll = () => {
      const offsetTop = 10 // 초기 상단 여백
      menuTop.value = Math.max(offsetTop, window.scrollY) // 스크롤에 따라 top 값 변경
    }

    onMounted(() => {
      window.addEventListener('scroll', handleScroll)
    })

    onUnmounted(() => {
      window.removeEventListener('scroll', handleScroll)
    })

    const navigateTo = (path) => {
      router.push(path)
    }

    return { navigateTo, menuTop }
  }
}
</script>

<style>
#app {
  display: flex;
  height: 100%;
}

.left-menu {
  width: 200px;
  background: #f4f4f4;
  padding: 20px;
  position: absolute;
  font-family: 'Arial', sans-serif;
  left: 0;
  border: 3px solid #90EE90;
  background: #ffffff; /* 흰색 배경 */
  border-radius: 10px;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1); /* 그림자 강조 */
}

.left-menu ul {
  list-style: none;
  padding: 0;
}

.left-menu li {
  padding: 10px;
  cursor: pointer;
}

.left-menu li:hover {
  background: #ddd;
}

.right-content {
  flex: 1;
  padding: 20px;
  margin-left: 220px;
}
</style>
