import { createRouter, createWebHistory } from 'vue-router'
import MainPage from '../components/MainPage'
import DBList from '../components/DBList'
import SmsHistory from '../components/SmsHistory'
import ThresholdList from '../components/ThresholdList'
import TablespacesList from '../components/TablespacesList' // 수정된 컴포넌트 이름
import DailyChkView from '../components/DailyChk' // 수정된 컴포넌트 이름
import Login from '@/views/Login.vue'
import { useAuthStore } from '@/stores/authStore'

const routes = [
  { path: '/', component: Login }, // Login을 '/' 경로에 매핑
  { path: '/main', component: MainPage },
  { path: '/db-list', name: 'DBList', component: DBList },
  { path: '/sms-history', name: 'SmsHistory', component: SmsHistory },
  { path: '/threshold-list', name: 'ThresholdList', component: ThresholdList },
  { path: '/tablespaces', name: 'TablespacesList', component: TablespacesList }, // 경로 및 컴포넌트 이름 수정
  { path: '/dailyChk', name: 'DailyChkView', component: DailyChkView } // 경로 및 컴포넌트 이름 수정
]

const router = createRouter({
  history: createWebHistory(),
  routes
})

// 인증 상태 체크
router.beforeEach((to, from, next) => {
  const authStore = useAuthStore() // 인증 상태 가져오기
  if (to.path !== '/' && !authStore.token) {
    next('/') // 로그인되지 않으면 '/' 경로로 이동
  } else {
    next() // 로그인된 경우 계속 진행
  }
})

export default router
